// ============================================
// ELEMENT 01: TUBE MOUNT
// ============================================
// Adaptateur simple pour le T-rail du clamp
// Tient un tube vertical avec bearing 608
// ============================================

$fn = 64;

// ============================================
// PARAMETRES
// ============================================

/* [T-Rail Interface] */
// Mesur√© depuis adaptor.stl du Universal Modular Clamp
rail_width = 12;
rail_lip_depth = 2;
rail_total_depth = 4;
tolerance = 0.2;

/* [Base] */
base_length = 40;          // Longueur sur le rail
base_width = 40;           // Largeur totale
base_thickness = 6;        // √âpaisseur de la base

/* [Stud Slot] */
stud_slot_w = 3.2;
stud_slot_l = 12;

/* [Bearing 608] */
bearing_od = 22;
bearing_id = 8;
bearing_h = 7;
bearing_tol = 0.15;

/* [Tube] */
tube_od = 22;              // = bearing OD (le tube s'appuie sur le bearing)
tube_id = 16;              // Int√©rieur du tube

// ============================================
// MODULES
// ============================================

module tube_mount() {

    housing_od = bearing_od + 8;
    housing_h = bearing_h + 4;

    difference() {
        union() {
            // === T-RAIL FOOT ===
            // Partie basse (dans le slot)
            translate([base_width/2 - rail_width/2, 0, 0])
                cube([rail_width - tolerance, base_length, rail_total_depth - rail_lip_depth]);

            // Partie haute (sous la l√®vre du rail)
            translate([0, 0, rail_total_depth - rail_lip_depth])
                cube([base_width - tolerance, base_length, rail_lip_depth]);

            // === BASE PLATE ===
            translate([0, 0, rail_total_depth])
                cube([base_width, base_length, base_thickness]);

            // === BEARING HOUSING ===
            translate([base_width/2, base_length/2, rail_total_depth + base_thickness])
                cylinder(d = housing_od, h = housing_h);
        }

        // === CUTOUTS ===

        // Stud slot (pour fixation M3)
        translate([base_width/2 - stud_slot_w/2, base_length/2 - stud_slot_l/2, -1])
            cube([stud_slot_w, stud_slot_l, rail_total_depth + 2]);

        // Bearing pocket (depuis le haut)
        translate([base_width/2, base_length/2, rail_total_depth + base_thickness + 2])
            cylinder(d = bearing_od + bearing_tol, h = bearing_h + 1);

        // Trou central (pour l'axe)
        translate([base_width/2, base_length/2, -1])
            cylinder(d = bearing_id, h = 50);
    }
}

// ============================================
// VISUALISATION
// ============================================

module bearing_visual() {
    color("Silver")
    translate([base_width/2, base_length/2, rail_total_depth + base_thickness + 2])
        difference() {
            cylinder(d = bearing_od, h = bearing_h);
            translate([0, 0, -1])
                cylinder(d = bearing_id, h = bearing_h + 2);
        }
}

module tube_visual() {
    color("Orange", 0.7)
    translate([base_width/2, base_length/2, 0])
        difference() {
            cylinder(d = tube_od, h = 120);
            translate([0, 0, -1])
                cylinder(d = tube_id, h = 122);
        }
}

// ============================================
// RENDER
// ============================================

color("RoyalBlue")
tube_mount();

// D√©commenter pour voir avec bearing et tube:
bearing_visual();
tube_visual();

// ============================================
// EXPORT
// ============================================

module export_tube_mount() {
    tube_mount();
}

// ============================================
// DIMENSIONS
// ============================================

