// ============================================
// PARAMETRES
// ============================================

/* [Espacement vue √©clat√©e] */
explode = 25;              // Distance entre les pi√®ces

/* [T-Rail Interface] */
rail_width = 12;
rail_lip_depth = 2;
rail_total_depth = 4;
tolerance = 0.2;

/* [Base] */
base_length = 40;
base_width = 40;
base_thickness = 6;

/* [Stud Slot] */
stud_slot_w = 3.2;
stud_slot_l = 12;

/* [Bearing 608] */
bearing_od = 22;
bearing_id = 8;
bearing_h = 7;

/* [Tube] */
tube_od = 22;
tube_id = 16;
tube_length = 100;

// ============================================
// COMPOSANTE 1: T-RAIL FOOT
// ============================================

module comp_1_rail_foot() {
    color("RoyalBlue")
    difference() {
        union() {
            // Partie basse (dans le slot)
            translate([base_width/2 - rail_width/2, 0, 0])
                cube([rail_width - tolerance, base_length, rail_total_depth - rail_lip_depth]);

            // Partie haute (sous la l√®vre)
            translate([0, 0, rail_total_depth - rail_lip_depth])
                cube([base_width - tolerance, base_length, rail_lip_depth]);
        }

        // Stud slot
        translate([base_width/2 - stud_slot_w/2, base_length/2 - stud_slot_l/2, -1])
            cube([stud_slot_w, stud_slot_l, rail_total_depth + 2]);
    }
}

// ============================================
// COMPOSANTE 2: BASE PLATE
// ============================================

module comp_2_base_plate() {
    color("Crimson")
    difference() {
        cube([base_width, base_length, base_thickness]);

        // Trou central
        translate([base_width/2, base_length/2, -1])
            cylinder(d = bearing_id, h = base_thickness + 2);
    }
}

// ============================================
// COMPOSANTE 3: BEARING HOUSING
// ============================================

module comp_3_bearing_housing() {
    housing_od = bearing_od + 8;
    housing_h = bearing_h + 4;

    color("ForestGreen")
    difference() {
        cylinder(d = housing_od, h = housing_h);

        // Bearing pocket
        translate([0, 0, 2])
            cylinder(d = bearing_od + 0.15, h = bearing_h + 1);

        // Trou central
        translate([0, 0, -1])
            cylinder(d = bearing_id, h = housing_h + 2);
    }
}

// ============================================
// COMPOSANTE 4: BEARING 608 (achet√©)
// ============================================

module comp_4_bearing() {
    color("Silver")
    difference() {
        cylinder(d = bearing_od, h = bearing_h);
        translate([0, 0, -1])
            cylinder(d = bearing_id, h = bearing_h + 2);
    }
}

// ============================================
// COMPOSANTE 5: TUBE (√† imprimer ou PVC)
// ============================================

module comp_5_tube() {
    color("Orange")
    difference() {
        cylinder(d = tube_od, h = tube_length);
        translate([0, 0, -1])
            cylinder(d = tube_id, h = tube_length + 2);
    }
}

// ============================================
// COMPOSANTE 6: STUD M3 (du clamp)
// ============================================

module comp_6_stud() {
    color("Gold")
    translate([0, 0, 0])
        cube([stud_slot_w - 0.5, stud_slot_l - 2, 3], center = true);
}

// ============================================
// VUE √âCLAT√âE
// ============================================

module exploded_view() {

    // Niveau 0: Stud (dans le rail du clamp)
    translate([base_width/2, base_length/2, -explode])
        comp_6_stud();

    // Niveau 1: T-Rail Foot
    translate([0, 0, 0])
        comp_1_rail_foot();

    // Niveau 2: Base Plate
    translate([0, 0, rail_total_depth + explode])
        comp_2_base_plate();

    // Niveau 3: Bearing Housing
    translate([base_width/2, base_length/2, rail_total_depth + base_thickness + explode * 2])
        comp_3_bearing_housing();

    // Niveau 4: Bearing 608
    translate([base_width/2, base_length/2, rail_total_depth + base_thickness + bearing_h + 4 + explode * 3])
        comp_4_bearing();

    // Niveau 5: Tube
    translate([base_width/2, base_length/2, rail_total_depth + base_thickness + bearing_h + 4 + bearing_h + explode * 4])
        comp_5_tube();
}

// ============================================
// VUE ASSEMBL√âE
// ============================================

module assembled_view() {

    // T-Rail Foot + Base (une seule pi√®ce imprim√©e)
    comp_1_rail_foot();
    translate([0, 0, rail_total_depth])
        comp_2_base_plate();
    translate([base_width/2, base_length/2, rail_total_depth + base_thickness])
        comp_3_bearing_housing();

    // Bearing (ins√©r√©)
    translate([base_width/2, base_length/2, rail_total_depth + base_thickness + 2])
        comp_4_bearing();

    // Tube (pos√© sur bearing)
    translate([base_width/2, base_length/2, rail_total_depth + base_thickness + 2])
        comp_5_tube();

    // Stud (en dessous)
    translate([base_width/2, base_length/2, -3])
        comp_6_stud();
}

// ============================================
// RENDER
// ============================================

// VUE √âCLAT√âE (d√©faut)
exploded_view();

