                // Partie TAB (entre dans le slot du rail)
                translate([(rail_base_width - rail_tab_width)/2, 0, 0])
                    cube([rail_tab_width - tolerance, rail_length, rail_tab_height]);

                // Partie BASE (repose sur le rail, plus large)
                translate([0, 0, rail_tab_height])
                    cube([rail_base_width - tolerance, rail_length, rail_base_height]);
            }

            // Slot pour le stud M3
            translate([rail_base_width/2 - stud_slot_w/2, rail_length/2 - stud_slot_l/2, -1])
                cube([stud_slot_w, stud_slot_l, rail_tab_height + 2]);
        }

        // AXE int√©gr√© (sur le dessus)
        translate([rail_base_width/2, rail_length/2, rail_tab_height + rail_base_height])
            cylinder(d = axle_dia - 0.2, h = axle_length);

        // Renfort autour de l'axe
        translate([rail_base_width/2, rail_length/2, rail_tab_height + rail_base_height])
            cylinder(d = 18, h = 8);
    }
}

// ============================================
