/*
 * ELEMENT 02: Hub Coulissant pour M√©canisme Umbrella
 *
 * Bas√© sur recherche Schacht Ultra + Strauch:
 * - Hub coulissant sur l'axe central (tube de Element 01)
 * - Points d'attache pour 6 struts (entretoises)
 * - M√©canisme de verrouillage (vis de blocage)
 * - Angle optimal struts: 70-75¬∞ vs poteau central
 *
 * S'ins√®re SUR le tube rotatif de Element 01
 */

// ==========================================
// PARAM√àTRES CONFIGURABLES
// ==========================================

/* [Tube de Element 01] */
tube_od = 25;              // Diam√®tre externe du tube (de Element 01)
hub_clearance = 0.4;       // Jeu pour glissement fluide

/* [Hub Principal] */
hub_height = 30;           // Hauteur du hub
hub_wall = 5;              // √âpaisseur paroi du hub
hub_od = tube_od + hub_clearance*2 + hub_wall*2;  // Diam√®tre externe calcul√©

/* [Points d'Attache Struts] */
strut_count = 6;           // Nombre de struts/bras
strut_pivot_dia = 5;       // Diam√®tre pivot pour strut (M5)
strut_boss_dia = 12;       // Diam√®tre du boss de renfort
strut_boss_height = 8;     // Hauteur du boss

/* [M√©canisme de Verrouillage] */
lock_screw_dia = 5;        // M5 vis de blocage
lock_nut_trap = 8.5;       // Largeur √©crou M5 (across flats + jeu)
lock_nut_depth = 4.5;      // Profondeur pi√®ge √©crou

/* [Qualit√© Rendu] */
$fn = 60;

// ==========================================
// CALCULS
// ==========================================

hub_id = tube_od + hub_clearance*2;
boss_radial_pos = hub_od/2 + strut_boss_dia/2 - 2;  // Position radiale des boss

// ==========================================
// COMPOSANTES (pour vue √©clat√©e)
// ==========================================

// COMPOSANTE 1: Hub principal avec pi√®ge √©crou
module comp_1_hub() {
    color("ForestGreen")
    difference() {
        union() {
            // Corps cylindrique principal
            cylinder(d = hub_od, h = hub_height);

            // Boss de renfort pour vis de blocage
            translate([hub_od/2 - hub_wall/2, 0, hub_height/2])
                rotate([0, 90, 0])
                    cylinder(d = lock_screw_dia + 8, h = hub_wall + 2);
        }

        // Trou central (pour glisser sur le tube)
        translate([0, 0, -0.1])
            cylinder(d = hub_id, h = hub_height + 0.2);

        // Trou pour vis de blocage
        translate([hub_id/2 - 1, 0, hub_height/2])
            rotate([0, 90, 0])
                cylinder(d = lock_screw_dia + 0.3, h = hub_wall + 10);

        // Pi√®ge √©crou hexagonal (ext√©rieur)
        translate([hub_od/2 - lock_nut_depth, 0, hub_height/2])
            rotate([0, 90, 0])
                cylinder(d = lock_nut_trap, h = lock_nut_depth + 0.1, $fn = 6);
    }
}

// COMPOSANTE 2: Anneau avec points d'attache struts
module comp_2_strut_ring() {
    color("Orange")
    difference() {
        union() {
            // Anneau qui se fixe sur le hub
            cylinder(d = hub_od + 4, h = 6);

            // Boss pour chaque strut
            for (i = [0:strut_count-1]) {
                angle = i * (360 / strut_count);
                rotate([0, 0, angle])
                    translate([boss_radial_pos, 0, 0])
                        cylinder(d = strut_boss_dia, h = strut_boss_height);
            }
        }

        // Trou central pour passer sur le hub
        translate([0, 0, -0.1])
            cylinder(d = hub_od + 0.4, h = 6 + 0.2);

        // Trous pivots pour struts
        for (i = [0:strut_count-1]) {
            angle = i * (360 / strut_count);
            rotate([0, 0, angle])
                translate([boss_radial_pos, 0, -0.1])
                    cylinder(d = strut_pivot_dia + 0.3, h = strut_boss_height + 0.2);
        }
    }
}

// COMPOSANTE 3: Vis de blocage (M5)
module comp_3_lock_screw() {
    color("Silver")
    union() {
        // T√™te de vis
        cylinder(d = 9, h = 3);
        // Filetage
        translate([0, 0, 3])
            cylinder(d = lock_screw_dia, h = hub_wall + 5);
    }
}

// COMPOSANTE 4: √âcrou M5
module comp_4_nut() {
    color("DimGray")
    cylinder(d = lock_nut_trap - 0.5, h = 4, $fn = 6);
}

// ==========================================
// ASSEMBLAGE
// ==========================================

module assembly() {
    // Hub principal
    comp_1_hub();

    // Anneau struts (au-dessus du hub)
    translate([0, 0, hub_height + 2])
        comp_2_strut_ring();

    // Vis de blocage (positionn√©e)
    translate([hub_od/2 + 10, 0, hub_height/2])
        rotate([0, -90, 0])
            comp_3_lock_screw();

    // √âcrou (dans le pi√®ge)
    translate([hub_od/2 - lock_nut_depth/2, 0, hub_height/2])
        rotate([0, 90, 0])
            comp_4_nut();
}

// ==========================================
// VUE √âCLAT√âE
// ==========================================

module exploded_view() {
    explode_z = 25;
