        translate([0, 0, 10])
            axle_fixed();
        translate([0, 0, tube_z])
            tube_rotating();
    }

    // ELEMENT 02: Hub + Anneau
    translate([0, 0, hub_z]) {
        hub_sliding();
        translate([0, 0, hub_height + 2])
            strut_ring();
    }

    // Point pivot des bras (sommet du tube)
    arm_pivot_z = tube_z + tube_length - 5;
    arm_pivot_radius = tube_od/2 + 10;

    // Hub attachment pour struts
    strut_hub_z = hub_z + hub_height + 8;
    strut_hub_radius = hub_od/2 + 6;

    // ELEMENT 03: Bras + Struts (√ó6)
    for (i = [0:arm_count-1]) {
        angle = i * 360/arm_count;

        // Bras
        rotate([0, 0, angle])
            translate([arm_pivot_radius, 0, arm_pivot_z])
                rotate([0, deploy_angle, 0])
                    arm();

        // Strut (connecte hub au bras)
        // Simplifi√© - juste pour visualisation
        rotate([0, 0, angle])
            translate([strut_hub_radius, 0, strut_hub_z])
                rotate([0, deploy_angle - 20, 0])
                    strut();
    }
}

