// Intelligent response generator - prioritizes custom COMPLETED messages
function generateIntelligentResponse(userQuery: string, assistantResponse: string, completedLine: string): string {
  // Clean the completed line
  const cleanCompleted = completedLine
    .replace(/\*+/g, '')
    .replace(/\[AGENT:\w+\]\s*/i, '')
    .trim();

  // If the completed line has meaningful custom content (not generic), use it
  const genericPhrases = [
    'completed successfully',
    'task completed',
    'done successfully',
    'finished successfully',
    'completed the task',
    'completed your request'
  ];

  const isGenericCompleted = genericPhrases.some(phrase =>
    cleanCompleted.toLowerCase() === phrase ||
    cleanCompleted.toLowerCase() === `${phrase}.`
  );

  // If we have a custom, non-generic completed message, prefer it
  if (!isGenericCompleted && cleanCompleted.length > 10) {
    return cleanCompleted;
  }

  // Extract key information from the full response
  const responseLC = assistantResponse.toLowerCase();
  const queryLC = userQuery.toLowerCase();

  // Only apply shortcuts for very specific simple cases

  // Simple thanks acknowledgment - high priority
  if (queryLC.match(/^(thank|thanks|awesome|great|good job|well done)[\s!?.]*$/i)) {
    return "You're welcome!";
  }

  // Simple math calculations - ONLY if it's just a calculation
  if (queryLC.match(/^\s*\d+\s*[\+\-\*\/]\s*\d+\s*\??$/)) {
    const resultMatch = assistantResponse.match(/=\s*(-?\d+(?:\.\d+)?)|(?:equals?|is)\s+(-?\d+(?:\.\d+)?)/i);
    if (resultMatch) {
      return resultMatch[1] || resultMatch[2];
    }
  }

  // Very simple yes/no - ONLY if the query is extremely simple
  if (queryLC.match(/^(is|are|was|were)\s+\w+\s+\w+\??$/i)) {
    if (cleanCompleted.toLowerCase() === 'yes' || cleanCompleted.toLowerCase() === 'no') {
      return cleanCompleted;
    }
  }

  // Simple time query - ONLY if asking for just the time
  if (queryLC.match(/^what\s+time\s+is\s+it\??$/i)) {
    const timeMatch = assistantResponse.match(/\d{1,2}:\d{2}(?::\d{2})?\s*(?:AM|PM)?/i);
    if (timeMatch) {
      return timeMatch[0];
    }
  }

  // For all other cases, use the actual completed message
  // This ensures custom messages are preserved
  return cleanCompleted;
}

async function main() {
  // Log that hook was triggered
  const timestamp = new Date().toISOString();
  console.error(`\nüé¨ STOP-HOOK TRIGGERED AT ${timestamp}`);

  // Get input
  let input = '';
  const decoder = new TextDecoder();
  const reader = Bun.stdin.stream().getReader();

  try {
    while (true) {
      const { done, value } = await reader.read();
      if (done) break;
      input += decoder.decode(value, { stream: true });
    }
  } catch (e) {
    console.error(`‚ùå Error reading input: ${e}`);
    process.exit(0);
  }

  if (!input) {
    console.error('‚ùå No input received');
    process.exit(0);
  }

  let transcriptPath;
  try {
    const parsed = JSON.parse(input);
    transcriptPath = parsed.transcript_path;
    console.error(`üìÅ Transcript path: ${transcriptPath}`);
  } catch (e) {
    console.error(`‚ùå Error parsing input JSON: ${e}`);
    process.exit(0);
  }

  if (!transcriptPath) {
    console.error('‚ùå No transcript_path in input');
    process.exit(0);
  }

  // Read the transcript
  let transcript;
  try {
    transcript = readFileSync(transcriptPath, 'utf-8');
    console.error(`üìú Transcript loaded: ${transcript.split('\n').length} lines`);
  } catch (e) {
    console.error(`‚ùå Error reading transcript: ${e}`);
    process.exit(0);
  }

  // Parse the JSON lines to find what happened in this session
  const lines = transcript.trim().split('\n');

  // Get the last user query for context
  let lastUserQuery = '';
  for (let i = lines.length - 1; i >= 0; i--) {
    try {
      const entry = JSON.parse(lines[i]);
      if (entry.type === 'user' && entry.message?.content) {
        // Extract text from user message
        const content = entry.message.content;
        if (typeof content === 'string') {
          lastUserQuery = content;
        } else if (Array.isArray(content)) {
          for (const item of content) {
            if (item.type === 'text' && item.text) {
              lastUserQuery = item.text;
              break;
            }
          }
        }
        if (lastUserQuery) break;
      }
    } catch (e) {
      // Skip invalid JSON
    }
  }

  // First, check if the LAST assistant message contains a Task tool or a COMPLETED line
  let isAgentTask = false;
  let taskResult = '';
  let agentType = '';

  // Find the last assistant message
  for (let i = lines.length - 1; i >= 0; i--) {
    try {
      const entry = JSON.parse(lines[i]);

      if (entry.type === 'assistant' && entry.message?.content) {
        // Check if this assistant message contains a Task tool_use
        let foundTask = false;
        const contentArray = Array.isArray(entry.message.content) ? entry.message.content : [entry.message.content];
        for (const content of contentArray) {
          if (content?.type === 'tool_use' && content.name === 'Task') {
            // This is an agent task - find its result
            foundTask = true;
            agentType = content.input?.subagent_type || '';

            // Find the corresponding tool_result
            for (let j = i + 1; j < lines.length; j++) {
              const resultEntry = JSON.parse(lines[j]);
              if (resultEntry.type === 'user' && resultEntry.message?.content) {
                const resultContentArray = Array.isArray(resultEntry.message.content)
                  ? resultEntry.message.content
                  : [resultEntry.message.content];
                for (const resultContent of resultContentArray) {
                  if (resultContent?.type === 'tool_result' && resultContent.tool_use_id === content.id) {
                    taskResult = contentToText(resultContent.content);
                    isAgentTask = true;
                    break;
                  }
                }
              }
              if (taskResult) break;
            }
            break;
          }
        }

        // We found the last assistant message, stop looking
        break;
      }
    } catch (e) {
      // Skip invalid JSON
    }
  }

  // Generate the announcement
  let message = '';
  let voiceConfig = VOICE_CONFIG.voices.main; // Default to main voice config
  let kaiHasCustomCompleted = false;

  // ALWAYS check Kai's response FIRST (even when agents are used)
  const lastResponse = lines[lines.length - 1];
  try {
    const entry = JSON.parse(lastResponse);
    if (entry.type === 'assistant' && entry.message?.content) {
      const content = contentToText(entry.message.content);

      // First, look for CUSTOM COMPLETED line (voice-optimized)
      const customCompletedMatch = content.match(/üó£Ô∏è\s*CUSTOM\s+COMPLETED:\s*(.+?)(?:\n|$)/im);

      if (customCompletedMatch) {
        // Get the custom voice response
        let customText = customCompletedMatch[1].trim()
          .replace(/\[.*?\]/g, '') // Remove bracketed text like [Optional: ...]
          .replace(/\*+/g, '') // Remove asterisks
          .trim();

        // Use custom completed if it's under 8 words
        const wordCount = customText.split(/\s+/).length;
        if (customText && wordCount <= 8) {
          message = customText;
          kaiHasCustomCompleted = true;
          console.error(`üó£Ô∏è MAIN CUSTOM VOICE: ${message}`);
        } else {
          // Custom completed too long, fall back to regular COMPLETED
          const completedMatch = content.match(/üéØ\s*COMPLETED:\s*(.+?)(?:\n|$)/im);
          if (completedMatch) {
            let completedText = completedMatch[1].trim();
            message = generateIntelligentResponse(lastUserQuery, content, completedText);
            console.error(`üéØ MAIN FALLBACK (custom too long): ${message}`);
          }
        }
      } else if (!isAgentTask) {
        // No CUSTOM COMPLETED and no agent - look for regular COMPLETED line
        const completedMatch = content.match(/üéØ\s*COMPLETED:\s*(.+?)(?:\n|$)/im);

        if (completedMatch) {
          // Get the raw text after the colon
          let completedText = completedMatch[1].trim();

          // Generate intelligent response
          message = generateIntelligentResponse(lastUserQuery, content, completedText);

          console.error(`üéØ MAIN INTELLIGENT: ${message}`);
        } else {
          // No COMPLETED line found - don't send anything
          console.error('‚ö†Ô∏è No COMPLETED line found');
        }
      }
    }
  } catch (e) {
    console.error('‚ö†Ô∏è Error parsing assistant response:', e);
  }

  // If Kai didn't provide a CUSTOM COMPLETED and an agent was used, check agent's response
  if (!message && isAgentTask && taskResult) {
    // First, try to find CUSTOM COMPLETED line in agent response
    const customCompletedMatch = taskResult.match(/üó£Ô∏è\s*CUSTOM\s+COMPLETED:\s*(.+?)(?:\n|$)/im);

    if (customCompletedMatch) {
      // Get the custom voice response
      let customText = customCompletedMatch[1].trim()
        .replace(/\[.*?\]/g, '') // Remove bracketed text
        .replace(/\*+/g, '') // Remove asterisks
        .replace(/\[AGENT:\w+\]\s*/i, '') // Remove agent tags
        .trim();

      // Use custom completed if it's under 8 words
      const wordCount = customText.split(/\s+/).length;
      if (customText && wordCount <= 8) {
        message = customText;
        voiceConfig = VOICE_CONFIG.voices[agentType.toLowerCase()] || VOICE_CONFIG.voices.main;
        console.error(`üó£Ô∏è AGENT CUSTOM VOICE (fallback): ${message}`);
      } else {
        // Custom completed too long, fall back to regular COMPLETED
        const completedMatch = taskResult.match(/üéØ\s*COMPLETED:\s*(.+?)$/im);
        if (completedMatch) {
          let completedText = completedMatch[1].trim()
            .replace(/\*+/g, '')
            .replace(/\[AGENT:\w+\]\s*/i, '')
            .trim();
          message = generateIntelligentResponse(lastUserQuery, taskResult, completedText);
          voiceConfig = VOICE_CONFIG.voices[agentType.toLowerCase()] || VOICE_CONFIG.voices.main;
          console.error(`üéØ AGENT FALLBACK (custom too long): ${message}`);
        }
      }
    } else {
      // No CUSTOM COMPLETED, look for regular COMPLETED line
      const completedMatch = taskResult.match(/üéØ\s*COMPLETED:\s*(.+?)$/im);

      if (completedMatch) {
        // Get exactly what the agent said after COMPLETED:
        let completedText = completedMatch[1].trim();

        // Remove markdown formatting
        completedText = completedText
          .replace(/\*+/g, '')  // Remove asterisks
          .replace(/\[AGENT:\w+\]\s*/i, '') // Remove agent tags
          .trim();

        // Generate intelligent response for agent tasks
        message = generateIntelligentResponse(lastUserQuery, taskResult, completedText);
        voiceConfig = VOICE_CONFIG.voices[agentType.toLowerCase()] || VOICE_CONFIG.voices.main;

        console.error(`üéØ AGENT INTELLIGENT (fallback): ${message}`);
      }
    }
  }

  // FIRST: Send voice notification if we have a message
  if (message) {
    // Align voice payload with initialize-pai-session.ts (prefer voice_id)
    const voiceId = process.env.DA_VOICE_ID || 'default-voice-id';
    const priority = 'low';
    // Send to voice server
    await fetch('http://localhost:8888/notify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        title: 'Completion',
        message,
        voice_enabled: true,
        priority,
        voice_id: voiceId,
        // keep legacy fields for compatibility with voice server configs that use names/rates
        voice_name: voiceConfig.voice_name,
        rate: voiceConfig.rate_wpm
      })
    }).catch(() => {});
    console.error(`üîä Voice notification sent: "${message}" with voice: ${voiceConfig.voice_name} at ${voiceConfig.rate_wpm} wpm (${voiceConfig.rate_multiplier}x)`);
  }

  // ALWAYS set tab title to override any previous titles (like "dynamic requirements")
  // Generate a meaningful title even if we don't have a voice message
  let tabTitle = message || '';

  // If we don't have a message, generate a title from the last user query or completed task
  if (!tabTitle && lastUserQuery) {
    // Try to extract a completed line from the last assistant response
    try {
      const lastResponse = lines[lines.length - 1];
      const entry = JSON.parse(lastResponse);
      if (entry.type === 'assistant' && entry.message?.content) {
        const content = contentToText(entry.message.content);
        const completedMatch = content.match(/üéØ\s*COMPLETED:\s*(.+?)(?:\n|$)/im);
        if (completedMatch) {
          tabTitle = completedMatch[1].trim()
            .replace(/\*+/g, '')
            .replace(/\[.*?\]/g, '')
            .trim();
        }
      }
    } catch (e) {}

    // Fall back to generating a title from the user query
    if (!tabTitle) {
      tabTitle = generateTabTitle(lastUserQuery, '');
    }
  }

  // Set tab title to override "dynamic requirements" or any other previous title
  if (tabTitle) {
    try {
      // Escape single quotes in the message to prevent shell injection
      const escapedTitle = tabTitle.replace(/'/g, "'\\''");

      // Use printf command to set the tab title - this works in Kitty
      const { execSync } = await import('child_process');
      execSync(`printf '\\033]0;${escapedTitle}\\007' >&2`);
      execSync(`printf '\\033]2;${escapedTitle}\\007' >&2`);
      execSync(`printf '\\033]30;${escapedTitle}\\007' >&2`);

      console.error(`\nüè∑Ô∏è Tab title set to: "${tabTitle}"`);
    } catch (e) {
      console.error(`‚ùå Failed to set tab title: ${e}`);
    }
  }

  console.error(`üìù User query: ${lastUserQuery || 'No query found'}`);
  console.error(`‚úÖ Message: ${message || 'No completion message'}`)

  // Final tab title override as the very last action - use the actual completion message
  if (message) {
    // Use the actual completion message as the tab title
    const finalTabTitle = message.slice(0, 50); // Limit to 50 chars for tab title
    process.stderr.write(`\033]2;${finalTabTitle}\007`);
  }

  console.error(`üé¨ STOP-HOOK COMPLETED SUCCESSFULLY at ${new Date().toISOString()}\n`);
}

main().catch(() => {});

#!/usr/bin/env bun

/**
 * SessionEnd Hook - Captures session summary for UOCS
 *
 * Generates a session summary document when a Claude Code session ends,
 * documenting what was accomplished during the session.
 */

import { writeFileSync, mkdirSync, existsSync, readFileSync, readdirSync } from 'fs';
import { join } from 'path';
import { PAI_DIR, HISTORY_DIR } from './lib/pai-paths';

interface SessionData {
  conversation_id: string;
  timestamp: string;
  [key: string]: any;
}

async function main() {
  try {
    // Read input from stdin
    const input = await Bun.stdin.text();
    if (!input || input.trim() === '') {
      process.exit(0);
    }

    const data: SessionData = JSON.parse(input);

    // Generate timestamp for filename
    const now = new Date();
    const timestamp = now.toISOString()
      .replace(/:/g, '')
      .replace(/\..+/, '')
      .replace('T', '-'); // YYYY-MM-DD-HHMMSS

    const yearMonth = timestamp.substring(0, 7); // YYYY-MM

    // Try to extract session info from raw outputs
    const sessionInfo = await analyzeSession(data.conversation_id, yearMonth);

    // Generate filename
    const filename = `${timestamp}_SESSION_${sessionInfo.focus}.md`;

    // Ensure directory exists
    const sessionDir = join(HISTORY_DIR, 'sessions', yearMonth);
    if (!existsSync(sessionDir)) {
      mkdirSync(sessionDir, { recursive: true });
    }

    // Generate session document
    const sessionDoc = formatSessionDocument(timestamp, data, sessionInfo);

    // Write session file
    writeFileSync(join(sessionDir, filename), sessionDoc);

    // Exit successfully
    process.exit(0);
  } catch (error) {
    // Silent failure - don't disrupt workflow
    console.error(`[UOCS] SessionEnd hook error: ${error}`);
    process.exit(0);
  }
}

async function analyzeSession(conversationId: string, yearMonth: string): Promise<any> {
  // Try to read raw outputs for this session
  const rawOutputsDir = join(HISTORY_DIR, 'raw-outputs', yearMonth);

  let filesChanged: string[] = [];
  let commandsExecuted: string[] = [];
  let toolsUsed: Set<string> = new Set();

  try {
    if (existsSync(rawOutputsDir)) {
      const files = readdirSync(rawOutputsDir).filter(f => f.endsWith('.jsonl'));

      for (const file of files) {
        const filePath = join(rawOutputsDir, file);
        const content = readFileSync(filePath, 'utf-8');
        const lines = content.split('\n').filter(l => l.trim());

        for (const line of lines) {
          try {
            const entry = JSON.parse(line);
            if (entry.session === conversationId) {
              toolsUsed.add(entry.tool);

              // Extract file changes
              if (entry.tool === 'Edit' || entry.tool === 'Write') {
                if (entry.input?.file_path) {
                  filesChanged.push(entry.input.file_path);
                }
              }

              // Extract bash commands
              if (entry.tool === 'Bash' && entry.input?.command) {
                commandsExecuted.push(entry.input.command);
              }
            }
          } catch (e) {
            // Skip invalid JSON lines
          }
        }
      }
    }
  } catch (error) {
    // Silent failure
  }

  return {
    focus: 'general-work',
    filesChanged: [...new Set(filesChanged)].slice(0, 10), // Unique, max 10
    commandsExecuted: commandsExecuted.slice(0, 10), // Max 10
    toolsUsed: Array.from(toolsUsed),
    duration: 0 // Unknown
  };
}

function formatSessionDocument(timestamp: string, data: SessionData, info: any): string {
  const date = timestamp.substring(0, 10); // YYYY-MM-DD
  const time = timestamp.substring(11).replace(/-/g, ':'); // HH:MM:SS

  return `---
capture_type: SESSION
timestamp: ${new Date().toISOString()}
session_id: ${data.conversation_id}
duration_minutes: ${info.duration}
executor: kai
---

# Session: ${info.focus}

**Date:** ${date}
**Time:** ${time}
**Session ID:** ${data.conversation_id}

---

## Session Overview

**Focus:** General development work
**Duration:** ${info.duration > 0 ? `${info.duration} minutes` : 'Unknown'}

---

## Tools Used

${info.toolsUsed.length > 0 ? info.toolsUsed.map((t: string) => `- ${t}`).join('\n') : '- None recorded'}

---

## Files Modified

${info.filesChanged.length > 0 ? info.filesChanged.map((f: string) => `- \`${f}\``).join('\n') : '- None recorded'}

**Total Files Changed:** ${info.filesChanged.length}

---

## Commands Executed

${info.commandsExecuted.length > 0 ? '```bash\n' + info.commandsExecuted.join('\n') + '\n```' : 'None recorded'}

---

## Notes

This session summary was automatically generated by the UOCS SessionEnd hook.

For detailed tool outputs, see: \`\${PAI_DIR}/history/raw-outputs/${timestamp.substring(0, 7)}/\`

---

**Session Outcome:** Completed
**Generated:** ${new Date().toISOString()}
`;
}

main();

/**
 * PAI Path Resolution - Single Source of Truth
 *
 * This module provides consistent path resolution across all PAI hooks.
 * It handles PAI_DIR detection whether set explicitly or defaulting to ~/.claude
 *
 * Usage in hooks:
 *   import { PAI_DIR, HOOKS_DIR, SKILLS_DIR } from './lib/pai-paths';
 */

import { homedir } from 'os';
import { resolve, join } from 'path';
import { existsSync } from 'fs';

/**
 * Smart PAI_DIR detection with fallback
 * Priority:
 * 1. PAI_DIR environment variable (if set)
 * 2. ~/.claude (standard location)
 */
export const PAI_DIR = process.env.PAI_DIR
  ? resolve(process.env.PAI_DIR)
  : resolve(homedir(), '.claude');

/**
 * Common PAI directories
 */
export const HOOKS_DIR = join(PAI_DIR, 'hooks');
export const SKILLS_DIR = join(PAI_DIR, 'skills');
export const AGENTS_DIR = join(PAI_DIR, 'agents');
export const HISTORY_DIR = join(PAI_DIR, 'History');
export const COMMANDS_DIR = join(PAI_DIR, 'commands');

/**
 * Validate PAI directory structure on first import
 * This fails fast with a clear error if PAI is misconfigured
 */
function validatePAIStructure(): void {
  if (!existsSync(PAI_DIR)) {
    console.error(`‚ùå PAI_DIR does not exist: ${PAI_DIR}`);
    console.error(`   Expected ~/.claude or set PAI_DIR environment variable`);
    process.exit(1);
  }

  if (!existsSync(HOOKS_DIR)) {
    console.error(`‚ùå PAI hooks directory not found: ${HOOKS_DIR}`);
    console.error(`   Your PAI_DIR may be misconfigured`);
    console.error(`   Current PAI_DIR: ${PAI_DIR}`);
    process.exit(1);
  }
}

// Run validation on module import
// This ensures any hook that imports this module will fail fast if paths are wrong
validatePAIStructure();

/**
 * Helper to get history file path with date-based organization
 */
export function getHistoryFilePath(subdir: string, filename: string): string {
  const now = new Date();
  const pstDate = new Date(now.toLocaleString('en-US', { timeZone: process.env.TIME_ZONE || 'America/Los_Angeles' }));
  const year = pstDate.getFullYear();
  const month = String(pstDate.getMonth() + 1).padStart(2, '0');

  return join(HISTORY_DIR, subdir, `${year}-${month}`, filename);
}

